require 'trollop'require 'Bento/Confetti'$NonMergeableAttributeName = "NonMergeable"$NonMergeableAttributeValue = "1"opts = Trollop::options do	version "MarkNonMergeable 1.0 - marks a file as non-mergeable for CC merges"	opt :file, "File name" , :type => String	opt :verbose,  "Be verbose"	 , :default => false	opt :noop,	"do nothing, just print" , :default => false	endTrollop::die "Must specify a file name" if !opts[:file]if opts[:verbose]	$VerboseMode = true	puts "Entering verbose mode"	puts "File name: #{opts[:file]}"	puts "NoOp flag: #{opts[:noop]}"end filename = opts[:file]noOp = opts[:noop]if !noOp	element = ClearCASE::Element.new(filename)	element.set_attribute($NonMergeableAttributeName, $NonMergeableAttributeValue)end